CREATE PROCEDURE [SEIDR].[JobReport_iu]
	@JobReportID int out,	
	@ReportName varchar(130) = null,
	@SQLProcedure varchar(258) = null,
	@Recipient varchar(500) = null,
	@ArchiveFolder varchar(300) = null,
	@Mode tinyint = null,
	@LastExecution datetime = null,
	@DatabaseLookupID int = null,
	@DatabaseLookupDescription varchar(70) = null
AS

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
IF 1 < (SELECT COUNT(*) FROM INSERTED i JOIN SEIDR.ScheduleRuleCluster_ScheduleRule srcsr ON i.ScheduleRuleID = srcsr.ScheduleRuleID) BEGIN ROLLBACK RAISERROR('ScheduleRule is in use by multiple ScheduleRuleClusters. Cannot alter.', 16, 1); RETURN END
INSERT INTO [SEIDR].[ScheduleRuleHistory] ([Description] ,[PartOfDateType] ,[PartOfDate] ,[IntervalType] ,[IntervalValue] ,[Hour] ,[Minute] ,[Modifier] ,[DD] ,[ArchiveTime] ,[ScheduleRuleID]) SELECT d.Description ,d.PartOfDateType ,d.PartOfDate ,d.IntervalType ,d.IntervalValue ,d.[Hour] ,d.[Minute] ,SUSER_NAME() as Modifier ,d.DD ,GETDATE() ,d.ScheduleRuleID FROM DELETED d JOIN INSERTED i ON d.ScheduleRuleID = i.ScheduleRuleID WHERE ISNULL(i.Description, '') <> d.Description OR ISNULL(i.PartOfDateType, '') <> ISNULL(d.PartOfDateType, '') OR ISNULL(i.PartOfDate, '') <> ISNULL(d.PartOfDate, '') OR ISNULL(i.Hour, 0) <> ISNULL(d.Hour, 0) OR ISNULL(i.Minute, 0) <> ISNULL(d.Minute, 0) OR i.Active <> d.Active END

