CREATE PROCEDURE SEIDR.usp_Job_Validate
	@JobList SEIDR.udt_JobMetaData readonly
as

BEGIN SELECT @rollback_query=[RollbackCommand] from [SEIDR].[vw_JobExecution_Rollback_Helper] where JobExecution_ExecutionStatusID=@JobExecution_ExecutionStatusID
SET @rollback_query = RIGHT(@rollback_query, LEN(@rollback_query) - 5) ;
EXECUTE (@rollback_query); SET @Rollback_query = null --No reason to provide this to the UI. END

